import React, { useState, useEffect } from 'react';
import './PatientDetail.css';
import { supabase } from '../supabaseClient';

const PatientDetail = ({ patient, onClose }) => {
  const [appointments, setAppointments] = useState([]);
  const [reports, setReports] = useState([]);

  useEffect(() => {
    if (patient) {
      const fetchPatientData = async () => {
        // Fetch appointments
        const { data: appointmentsData, error: appointmentsError } = await supabase
          .from('appointments')
          .select('*')
          .eq('patient_id', patient.id)
          .order('appointment_at', { ascending: false });

        if (appointmentsError) {
          console.error('Error fetching appointments:', appointmentsError);
        } else {
          setAppointments(appointmentsData);
        }

        // Fetch reports
        const { data: reportsData, error: reportsError } = await supabase
          .from('reports')
          .select('*')
          .eq('patient_id', patient.id)
          .order('created_at', { ascending: false });

        if (reportsError) {
          console.error('Error fetching reports:', reportsError);
        } else {
          setReports(reportsData);
        }
      };

      fetchPatientData();
    }
  }, [patient]);

  if (!patient) {
    return null;
  }

  return (
    <div className="patient-detail">
      <div className="detail-header">
        <h2>{patient.name}</h2>
        <button onClick={onClose} className="close-btn">×</button>
      </div>
      <div className="detail-content">
        <div className="detail-section">
          <h3>Appointments</h3>
          <div className="card-list">
            {appointments.length > 0 ? (
              appointments.map(apt => (
                <div key={apt.id} className="card">
                  <p><strong>Date:</strong> {new Date(apt.appointment_at).toLocaleDateString()}</p>
                  <p><strong>AI Summary:</strong> {apt.ai_summary || 'N/A'}</p>
                  <p><strong>Doctor's Notes:</strong> {apt.doctor_notes || 'N/A'}</p>
                </div>
              ))
            ) : (
              <p>No appointments found.</p>
            )}
          </div>
        </div>
        <div className="detail-section">
          <h3>Reports</h3>
          <div className="card-list">
            {reports.length > 0 ? (
              reports.map(report => (
                <div key={report.id} className="card">
                  <p><strong>Date:</strong> {new Date(report.created_at).toLocaleDateString()}</p>
                  <p><strong>AI Summary:</strong> {report.ai_summary || 'N/A'}</p>
                  <p><strong>Doctor's Summary:</strong> {report.doctor_summary || 'N/A'}</p>
                </div>
              ))
            ) : (
              <p>No reports found.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PatientDetail;
