import React, { useState } from 'react';
import './Login.css';
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = "https://ngqlkfvxuxyfjigxilfn.supabase.co";
const supabaseAnonKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Im5ncWxrZnZ4dXh5ZmppZ3hpbGZuIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTk2MjA0ODgsImV4cCI6MjA3NTE5NjQ4OH0.DdQF_d_vL7xWo_iqo-6JpsOYAjIwo4zQ9nu22A04DrI";

const supabase = createClient(supabaseUrl, supabaseAnonKey);

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const { data, error } = await supabase
        .from('doctor')
        .select('*')
        .eq('email', email)
        .eq('password', password)
        .single();

      if (error || !data) {
        setError('Invalid email or password.');
        return;
      }

      // For a real app, you would handle session/token here
      console.log('Login successful:', data);
      // TODO: Redirect to dashboard
      window.location.href = '/'; // Simple redirect for now

    } catch (err) {
      setError('An unexpected error occurred. Please try again.');
      console.error('Login error:', err);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Doctor Login</h2>
        <form onSubmit={handleLogin}>
          <div className="input-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <p className="error-message">{error}</p>}
          <button type="submit" className="login-button">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
